func:
const buffered = context.get('buffered') || 0;
const after = msg._inlet ?
    buffered + 1 :
    buffered - msg.payload.length;
context.set('buffered', after);
msg.payload = {
    shape: "ring",
    fill: "blue",
    text: `buffered: ${after}`,
};
return msg;

const users = msg.payload;
const body = msg.req.body;
const maxId = users.length==0 ? 0 : Math.max(...users.map(user => user.id));
const user = {
    id: maxId + 1,
    name: body.name,
    age: Number(body.age)
}
msg.inserted_user = user;
msg.payload = user;
return msg;

const utils = {
    length: (x) => x.length,
    ohire: (s) => s + "***",
};
flow.set("utils", utils);
return msg;

const { length, ohire } = flow.get("utils");

const s = msg.payload;
msg.payload = {
    target: s,
    len: length(s),
    ohi: ohire(s),
}
return msg;

const name = msg.req.query.name;
const delay = Math.floor(Math.random() * 5 + 1) * 500;
msg.payload = {
    name: name,
    delay: delay,
};
msg.delay = delay;
return msg;

// キューの初期化要請
if(msg.reset){
    context.set("queue", []);
    context.set("running", false);
    return;
}

const q = context.get("queue");

// 後続処理の完了通知：キューが空でなければ先頭要素を放出する
if(msg.done){
    if(q.length > 0){
        const [next_msg, ...rest] = q;
        context.set("queue", rest);
        return next_msg;
    }
    else{
        context.set("running", false);
        return;
    }
}
// 新規メッセージの投入：処理中のメッセージがあればキューに追加し、なければそのまま流す
else{
    const running = context.get("running");
    if(running){
        context.set("queue", [...q, msg]);
        return;
    }
    else{
        context.set("running", true);
        return msg;
    }
}

if(msg.loop === undefined){
    const {ip_addr, port, sensor_devices} = msg.payload;
    msg.loop = {
        i: 0,
        ip_addr,
        port,
        sensor_devices,
    };
}
else{
    const { i, sensor_devices } = msg.loop;
    const len = sensor_devices.length;
    const next = i + 1;
    if (len === next) {
        return;
    }

    msg.loop.i = next;
}
return msg;

const { i, ip_addr, port, sensor_devices} = msg.loop;
const device = sensor_devices[i];
msg.payload = { name: `[${ip_addr}:${port}] ${device.no} ${device.words}` };
return msg;

msg.payload.now = msg.now;
const {i, sensor_devices} = msg.loop;
msg.payload.sensor_id = sensor_devices[i].sensor_id;
return msg;

