/**
 * topicごとの状態は以下の構造を持つ。
 * - runnningSince: 現在フローを流れているメッセージの送出時刻
 * - q: 未送出のメッセージ
 */
const INITIAL_Q = { runningSince: null, q: [] };

/** 送り出したメッセージが戻って来たのであればtrue */
const isReply = msg.nq !== undefined;

const qname = isReply ? msg.nq.name : msg.topic;
/**　今回対象としているキューの状態を上書きする */
function setState(runningSince, q) {
    context.set(qname, { runningSince, q });
}
/** 送出するメッセージに本カスタムノード固有のプロパティを設定する */
function addOwnProps(targetMsg) {
    targetMsg.nq = { runningSince: Date.now(), name: qname };
    return targetMsg;
}

const { runningSince, q } = context.get(qname) || INITIAL_Q;

if (isReply) {
    if (msg.nq.runningSince < runningSince) {
        // runningSinceは最後に送信した時刻である。
        // この条件を満たしたということは到着したメッセージより後に送信したものがあるということ。
        // つまり、今回到着したメッセージは既にタイムアウトになっており、
        // 現在は後続のメッセージが既にrunning状態だということである。
        // 到着したメッセージ自体はタイムアウト時点で本カスタムノードの出力2から送出済み。
        // 今回到着したメッセージは破棄する。
        // node.log(`[nq][$qname] dropped msg: ${msg}`);
        return;
    }
    else if (q.length > 0) {
        const [next, ...rest] = q;
        addOwnProps(next);
        setState(next.runningSince, rest);
        // node.log(`[nq][${qname}] dequeued msg: ${next}`);
        return [next, next];
    }
    else {
        setState(INITIAL_Q.runningSince, INITIAL_Q.q);
        // 送り出すメッセージがないのでtriggerノードのタイムアウトを取り消す
        // node.log(`[nq][${qname}] no msg in queue`);
        msg.reset = true;
        return [null, msg];
    }
}
else {
    if (runningSince === null) {
        addOwnProps(msg);
        setState(msg.runningSince, []);
        // node.log(`[nq][${qname}] pass-through: ${msg}`);
        return [msg, msg];
    }
    else {
        setState(runningSince, [...q, msg])
        // node.log(`[nq][${qname}] enqueued msg: ${msg}`);
        return;
    }
}

const mutexName = msg.mutexName || env.get("MUTEX_NAME");
const contextName = "__mutex_" + mutexName;
const mutex = global.get(contextName);
if(mutex){
    mutex.lock = false;
}
return msg;

const mutexName = env.get("MUTEX_NAME");
const timeoutSec = Number(env.get('TIMEOUT_SEC')) || 30;

const nowMsec = Date.now();
function lock(mutex){
    mutex.lock = true;
    mutex.expireAtMsec = nowMsec + timeoutSec * 1000;
}
function lockIsExpired(mutex){
    return mutex.lock && mutex.expireAtMsec < nowMsec;
}

const contextName = "__mutex_" + mutexName;

if(!global.get(contextName)){
    global.set(contextName, { lock: false });
}
const mutex = global.get(contextName);
const q = context.get("q");
function getStatus(){
    return {
        payload: `[${mutexName}] queued: ${q.length}, lock: ${mutex.lock}`
    };
}

const isControl = msg.isControl || msg.reset || msg.unlock;
const shouldUnlock = msg.reset || msg.unlock || lockIsExpired(mutex);
const shouldReset = msg.reset;

if (shouldUnlock) {
    mutex.lock = false;
}
if (shouldReset){
    q.length = 0;
}

if (isControl) {
    // 制御メッセージの場合
    if (!mutex.lock && q.length > 0) {
        // mutex空き & キューに要素あり → 先頭要素を送出しロック
        const nextMsg = q.shift();
        lock(mutex);
        return [nextMsg, null, getStatus()];
    }
    else{
        return [null, msg, getStatus()];
    }
}

// 通常メッセージの場合
msg.mutexName = mutexName;
if (mutex.lock) {
    // mutex使用中 → キューに格納
    q.push(msg);
    return [null, msg, getStatus()];
}
else{
    // mutex空き → 即時送出しmutexロック
    lock(mutex);
    return [msg, null, getStatus()];
}

